document.querySelectorAll("[data-countdown]").forEach((function(n){var o=n.getAttribute("data-countdown");function a(){var a=(new Date).getTime(),t=new Date(o)-a;if(t<=0)return clearInterval(s),void(n.innerHTML="Countdown expired");var e=Math.floor(t/864e5),c=Math.floor(t%864e5/36e5),p=Math.floor(t%36e5/6e4),u=Math.floor(t%6e4/1e3);n.innerHTML='<span class="countdown-section"><span class="countdown-amount hover-up">'+e+'</span><span class="countdown-period"> days </span></span><span class="countdown-section"><span class="countdown-amount hover-up">'+c+'</span><span class="countdown-period"> hours </span></span><span class="countdown-section"><span class="countdown-amount hover-up">'+p+'</span><span class="countdown-period"> mins </span></span><span class="countdown-section"><span class="countdown-amount hover-up">'+u+'</span><span class="countdown-period"> sec </span></span>'}a();var s=setInterval(a,1e3)}));