class PasswordToggler{constructor(s,e){this.passwordElements=document.getElementsByClassName(s),this.togglerElements=document.getElementsByClassName(e),this.attachEventListeners()}attachEventListeners(){for(let s=0;s<this.togglerElements.length;s++)this.togglerElements[s].addEventListener("click",(()=>{this.showHidePassword(s)}))}showHidePassword(s){const e=this.passwordElements[s],t=this.togglerElements[s];"password"===e.type?(e.setAttribute("type","text"),t.classList.add("bi-eye"),t.classList.remove("bi-eye-slash")):(t.classList.remove("bi-eye"),t.classList.add("bi-eye-slash"),e.setAttribute("type","password"))}}const passwordToggler=new PasswordToggler("fakePassword","passwordToggler");